'use strict';

/**
* Remove a directory entry.
*
* @module @stdlib/fs/unlink
*
* @example
* var unlink = require( '@stdlib/fs/unlink' );
*
* function done( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
*
* unlink( './beep/boop.txt', done );
*
* @example
* var unlinkSync = require( '@stdlib/fs/unlink' ).sync;
*
* var err = unlinkSync( './beep/boop.txt' );
* if ( err instanceof Error ) {
*     throw err;
* }
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var unlink = require( './main.js' );
var sync = require( './sync.js' );


// MAIN //

setReadOnly( unlink, 'sync', sync );


// EXPORTS //

module.exports = unlink;
