'use strict';

// MODULES //

var fs = require( 'fs' );


// MAIN //

/**
* Asynchronously removes a directory entry.
*
* @param {(string|Buffer|integer)} path - entry path
* @param {Function} clbk - callback to invoke after removing a directory entry
*
* @example
* function done( error ) {
*     if ( error ) {
*         throw error;
*     }
* }
*
* unlink( './beep/boop.txt', done );
*/
function unlink( path, clbk ) {
	fs.unlink( path, clbk );
}


// EXPORTS //

module.exports = unlink;
