'use strict';

// MODULES //

var tape = require( 'tape' );
var unlink = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof unlink, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to synchronously remove a directory entry', function test( t ) {
	t.strictEqual( typeof unlink.sync, 'function', 'has method' );
	t.end();
});
