'use strict';

// MODULES //

var tape = require( 'tape' );
var writeFile = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof writeFile, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to synchronously write data to a file', function test( t ) {
	t.strictEqual( typeof writeFile.sync, 'function', 'has method' );
	t.end();
});
