// TypeScript Version: 2.0

interface Iterator {
	/**
	* Returns an iterator protocol-compliant object containing the next iterated value (if one exists) and a boolean flag indicating whether the iterator is finished.
	*
	* @returns iterator protocol-compliant object
	*/
	next(): IteratorResult;

	/**
	* Finishes an iterator.
	*
	* @param value - value to return
	* @returns iterator protocol-compliant object
	*/
	return?( value?: any ): IteratorResult;
}

interface IterableIterator extends Iterator {
	/**
	* Returns a new iterable iterator.
	*
	* @returns iterable iterator
	*/
	[Symbol.iterator](): IterableIterator;
}

interface IteratorResult {
	/**
	* Iterated value (if one exists).
	*/
	value?: any;

	/**
	* Boolean flag indicating whether the iterator is finished.
	*/
	done: boolean;
}

/**
* Returns an iterator which iterates over the values of two or more iterators.
*
* ## Notes
*
* -   If an environment supports `Symbol.iterator` and all provided iterators are iterable, the returned iterator is iterable.
*
* @param iter0 - first iterator
* @param iter1 - second iterator
* @param iterN - subsequent iterators
* @returns iterator
*
* @example
*
* var array2iterator = require( `@stdlib/array/to-iterator` );
*
* var it1 = array2iterator( [ 1, 2 ] );
* var it2 = array2iterator( [ 3, 4 ] );
*
* var iter = iterConcat( it1, it2 );
*
* var v = iter.next().value;
* // returns 1
*
* v = iter.next().value;
* // returns 2
*
* v = iter.next().value;
* // returns 3
*
* v = iter.next().value;
* // returns 4
*
* var bool = iter.next().done;
* // returns true
*/
declare function iterConcat( iter0: Iterator | IterableIterator, iter1: Iterator | IterableIterator, ...iterN: Array<Iterator | IterableIterator> ): Iterator | IterableIterator; // tslint:disable-line:max-line-length


// EXPORTS //

export = iterConcat;
