#include "stdlib/lapack/base/dlacpy.h"
#include "stdlib/blas/base/shared.h"
#include "stdlib/strided/base/stride2offset.h"
#include "stdlib/math/base/special/fast/min.h"
#include "stdlib/ndarray/base/assert/is_row_major.h"

/**
* Copies all of a matrix `A` to another matrix `B`.
*
* @param M         number of rows in matrix `A`
* @param N         number of columns in matrix `A`
* @param A         input matrix
* @param strideA1  stride of the first dimension of `A`
* @param strideA2  stride of the second dimension of `A`
* @param offsetA   starting index for `A`
* @param B         output matrix
* @param strideB1  stride of the first dimension of `B`
* @param strideB2  stride of the second dimension of `B`
* @param offsetB   starting index for `B`
*/
void API_SUFFIX(c_copy_all)( const CBLAS_INT M, const CBLAS_INT N, const double *A, const CBLAS_INT strideA1, const CBLAS_INT strideA2, const CBLAS_INT offsetA, double *B, const CBLAS_INT strideB1, const CBLAS_INT strideB2, const CBLAS_INT offsetB ) {
	CBLAS_INT ia;
	CBLAS_INT ib;
	CBLAS_INT i0;
	CBLAS_INT i1;

	ia = offsetA;
	ib = offsetB;
	for ( i1 = 0; i1 < M; i1++ ) {
		for ( i0 = 0; i0 < N; i0++ ) {
			B[ ib+(i0*strideB2) ] = A[ ia+(i0*strideA2) ];
		}
		ia += strideA1;
		ib += strideB1;
	}
	return;
}

/**
* Copies all of a matrix `A` to another matrix `B`.
*
* @param M         number of rows in matrix `A`
* @param N         number of columns in matrix `A`
* @param A         input matrix
* @param strideA1  stride of the first dimension of `A`
* @param strideA2  stride of the second dimension of `A`
* @param offsetA   starting index for `A`
* @param B         output matrix
* @param strideB1  stride of the first dimension of `B`
* @param strideB2  stride of the second dimension of `B`
* @param offsetB   starting index for `B`
*/
void API_SUFFIX(c_copy_upper)( const CBLAS_INT M, const CBLAS_INT N, const double *A, const CBLAS_INT strideA1, const CBLAS_INT strideA2, const CBLAS_INT offsetA, double *B, const CBLAS_INT strideB1, const CBLAS_INT strideB2, const CBLAS_INT offsetB ) {
	CBLAS_INT ia;
	CBLAS_INT ib;
	CBLAS_INT i0;
	CBLAS_INT i1;

	CBLAS_INT strides[] = { strideA1, strideA2 };

	ia = offsetA;
	ib = offsetB;
	if ( stdlib_ndarray_is_row_major( 2, strides ) ) {
		for ( i1 = 0; i1 < M; i1++ ) {
			for ( i0 = i1; i0 < N; i0++ ) {
				B[ ib+(i0*strideB2) ] = A[ ia+(i0*strideA2) ];
			}
			ia += strideA1;
			ib += strideB1;
		}
		return;
	}

	for ( i1 = 0; i1 < N; i1++ ) {
		for ( i0 = 0; i0 <= stdlib_base_fast_min( i1, M-1 ); i0++ ) {
			B[ ib+(i0*strideB1) ] = A[ ia+(i0*strideA1) ];
		}
		ia += strideA2;
		ib += strideB2;
	}
	return;
}

/**
* Copies all of a matrix `A` to another matrix `B`.
*
* @param M         number of rows in matrix `A`
* @param N         number of columns in matrix `A`
* @param A         input matrix
* @param strideA1  stride of the first dimension of `A`
* @param strideA2  stride of the second dimension of `A`
* @param offsetA   starting index for `A`
* @param B         output matrix
* @param strideB1  stride of the first dimension of `B`
* @param strideB2  stride of the second dimension of `B`
* @param offsetB   starting index for `B`
*/
void API_SUFFIX(c_copy_lower)( const CBLAS_INT M, const CBLAS_INT N, const double *A, const CBLAS_INT strideA1, const CBLAS_INT strideA2, const CBLAS_INT offsetA, double *B, const CBLAS_INT strideB1, const CBLAS_INT strideB2, const CBLAS_INT offsetB ) {
	CBLAS_INT ia;
	CBLAS_INT ib;
	CBLAS_INT i0;
	CBLAS_INT i1;

	CBLAS_INT strides[] = { strideA1, strideA2 };

	ia = offsetA;
	ib = offsetB;
	if ( stdlib_ndarray_is_row_major( 2, strides ) ) {
		for ( i1 = 0; i1 < M; i1++ ) {
			for ( i0 = 0; i0 <= stdlib_base_fast_min( i1, N-1 ); i0++ ) {
				B[ ib+(i0*strideB2) ] = A[ ia+(i0*strideA2) ];
			}
			ia += strideA1;
			ib += strideB1;
		}
		return;
	}
	for ( i1 = 0; i1 < N; i1++ ) {
		for ( i0 = i1; i0 < M; i0++ ) {
			B[ ib+(i0*strideB1) ] = A[ ia+(i0*strideA1) ];
		}
		ia += strideA2;
		ib += strideB2;
	}
	return;
}

/**
* Copies all or part of a matrix `A` to another matrix `B`.
*
* @param order  storage layout of `A` and `B`
* @param uplo   specifies whether to copy the upper or lower triangular/trapezoidal part of matrix `A`
* @param M      number of rows in matrix `A`
* @param N      number of columns in matrix `A`
* @param A      input matrix
* @param LDA    stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
* @param B      output matrix
* @param LDB    stride of the first dimension of `B` (a.k.a., leading dimension of the matrix `B`)
*/
void API_SUFFIX(c_dlacpy)( const CBLAS_LAYOUT order, const CBLAS_UPLO uplo, const CBLAS_INT M, const CBLAS_INT N, const double *A, const CBLAS_INT LDA, double *B, const CBLAS_INT LDB ) {
	CBLAS_INT sa1;
	CBLAS_INT sa2;
	CBLAS_INT sb1;
	CBLAS_INT sb2;

	if ( order == CblasColMajor ) {
		sa1 = 1;
		sa2 = LDA;
		sb1 = 1;
		sb2 = LDB;
	} else { // order === 'row-major'
		if ( LDA < N ) {
			return;
		}
		if ( LDB < N ) {
			return;
		}
		sa1 = LDA;
		sa2 = 1;
		sb1 = LDB;
		sb2 = 1;
	}
	API_SUFFIX(c_dlacpy_ndarray)( uplo, M, N, A, sa1, sa2, 0, B, sb1, sb2, 0 );
	return;
}

/**
* Copies all or part of a matrix `A` to another matrix `B` using alternative indexing semantics.
*
* @private
* @param uplo     specifies whether to copy the upper or lower triangular/trapezoidal part of matrix `A`
* @param M        number of rows in matrix `A`
* @param N        number of columns in matrix `A`
* @param A        input matrix
* @param strideA1 stride of the first dimension of `A`
* @param strideA2 stride of the second dimension of `A`
* @param offsetA  starting index for `A`
* @param B        output matrix
* @param strideB1 stride of the first dimension of `B`
* @param strideB2 stride of the second dimension of `B`
* @param offsetB  starting index for `B`
*/
void API_SUFFIX(c_dlacpy_ndarray)( const CBLAS_UPLO uplo, const CBLAS_INT M, const CBLAS_INT N, const double *A, const CBLAS_INT strideA1, const CBLAS_INT strideA2, const CBLAS_INT offsetA, double *B, const CBLAS_INT strideB1, const CBLAS_INT strideB2, const CBLAS_INT offsetB ) {
	if ( uplo == CblasUpper ) {
		c_copy_upper( M, N, A, strideA1, strideA2, offsetA, B, strideB1, strideB2, offsetB );
		return;
	}
	if ( uplo == CblasLower ) {
		c_copy_lower( M, N, A, strideA1, strideA2, offsetA, B, strideB1, strideB2, offsetB );
		return;
	}
	c_copy_all( M, N, A, strideA1, strideA2, offsetA, B, strideB1, strideB2, offsetB );
	return;
}
