'use strict';

// MODULES //

var addon = require( './../src/addon.node' );


// MAIN //

/**
* Tests if a numeric value is `NaN`.
*
* @name isnan
* @type {Function}
* @param {number} x - value to test
* @returns {boolean} boolean indicating whether the value is `NaN`
*
* @example
* var bool = isnan( NaN );
* // returns true
*
* @example
* var bool = isnan( 7.0 );
* // returns false
*/
var isnan = addon.isnan;


// EXPORTS //

module.exports = isnan;
