/* global WebAssembly */
'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var instanceOf = require( '@stdlib/assert/instance-of' );
var readWASMSync = require( '@stdlib/fs/read-wasm' ).sync;


// VARIABLES //

var wasmBinaryFile = resolve( __dirname, '..', 'src', 'is_positive_zero.wasm' );


// MAIN //

/**
* Returns a function for testing if a numeric value is positive zero.
*
* @private
* @returns {Function} function to test if a numeric value is positive zero
*
* @example
* var isPositiveZero = load();
* // returns <Function>
*/
function load() {
	var imports;
	var wasm;
	var f;

	// Synchronously load the WebAssembly binary:
	wasm = readWASMSync( wasmBinaryFile );
	if ( instanceOf( wasm, Error ) ) {
		return;
	}
	// Synchronously create a WebAssembly module:
	wasm = new WebAssembly.Module( wasm );

	// Define module imports:
	imports = {};

	// Define imported global variables:
	imports.global = {
		'Infinity': Number.POSITIVE_INFINITY
	};

	// Define the imported module environment:
	imports.env = {};

	// Define the linear memory for the module:
	imports.env.memoryBase = 0;
	imports.env.memory = new WebAssembly.Memory({
		'initial': 256
	});

	// Define the module table for function look-ups:
	imports.env.tableBase = 0;
	imports.env.table = new WebAssembly.Table({
		'initial': 0,
		'element': 'anyfunc'
	});

	// Synchronously create a new WebAssembly module instance:
	wasm = new WebAssembly.Instance( wasm, imports );

	// Assign the exported function to a local alias:
	f = wasm.exports._stdlib_is_positive_zero; // eslint-disable-line no-underscore-dangle

	return isPositiveZero;

	/**
	* Tests if a numeric value is positive zero.
	*
	* @private
	* @param {number} x - value to test
	* @returns {boolean} boolean indicating whether the value is positive zero
	*/
	function isPositiveZero( x ) {
		return !!f( x );
	} // end FUNCTION isPositiveZero()
} // end FUNCTION load()


// EXPORTS //

module.exports = load();
