/**
* Test if a numeric value is positive zero.
*/
#include "stdlib_base_is_positive_zero.h"
#include <stdbool.h>
#include <math.h>

/**
* Tests if a numeric value is positive zero.
*
* @param x       number
* @return        boolean indicating if an input value is positive zero
*/
bool stdlib_base_is_positive_zero( const double x ) {
	return ( 1.0/x == INFINITY );
}
