'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var bench = require( '@stdlib/bench' );
var randu = require( '@stdlib/math/base/random/randu' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var pow = require( '@stdlib/math/base/special/pow' );
var tryRequire = require( '@stdlib/utils/try-require' );
var pkg = require( './../package.json' ).name;


// VARIABLES //

var dasum = tryRequire( resolve( __dirname, './../lib/dasum.wasm.js' ) );
var opts = {};
if ( dasum instanceof Error ) {
	opts.skip = true;
} else {
	dasum = dasum();
}


// FUNCTIONS //

/**
* Wraps `dasum`.
*
* @private
* @param {PositiveInteger} N - number of values to sum
* @param {Float64Array} x - input array
* @param {PositiveInteger} stride - `x` stride length
* @returns {number} sum
*/
function wrapper( N, x, stride ) {
	var nbytes;
	var heap;
	var s;

	// Determine the number of bytes:
	nbytes = x.length * x.BYTES_PER_ELEMENT;

	// Allocate space on the heap:
	heap = dasum.malloc( nbytes );

	// Copy the data to the heap:
	heap.set( new Uint8Array( x.buffer ) );

	// Compute the sum:
	s = dasum( N, heap, stride );

	// Free the memory:
	dasum.free( heap );

	return s;
} // end FUNCTION wrapper()

/**
* Creates a benchmark function.
*
* @private
* @param {PositiveInteger} len - array length
* @returns {Function} benchmark function
*/
function createBenchmark( len ) {
	var x;
	var i;

	x = new Float64Array( len );
	for ( i = 0; i < x.length; i++ ) {
		x[ i ] = ( randu()*10000.0 ) - 20000.0;
	}
	return benchmark;

	/**
	* Benchmark function.
	*
	* @private
	* @param {Benchmark} b - benchmark instance
	*/
	function benchmark( b ) {
		var y;
		var i;

		b.tic();
		for ( i = 0; i < b.iterations; i++ ) {
			y = wrapper( x.length, x, 1 );
			if ( isnan( y ) ) {
				b.fail( 'should not return NaN' );
			}
		}
		b.toc();
		if ( isnan( y ) ) {
			b.fail( 'should not return NaN' );
		} else {
			b.pass( 'benchmark finished' );
		}
		b.end();
	} // end FUNCTION benchmark()
} // end FUNCTION createBenchmark()


// MAIN //

/**
* Main execution sequence.
*
* @private
*/
function main() {
	var len;
	var min;
	var max;
	var f;
	var i;

	min = 1; // 10^min
	max = 6; // 10^max

	for ( i = min; i <= max; i++ ) {
		len = pow( 10, i );
		f = createBenchmark( len );
		bench( pkg+'::wasm:len='+len, opts, f );
	}
} // end FUNCTION main()

main();
