/**
* Example which demonstrates computing the sum of absolute values using the BLAS Level 1 routine `dasum`.
*/
#include <stdio.h>
#include "dasum.h"

#define N 5

int main() {
	int stride = 1;
	int n = N;
	int i;

	double X[ N ];
	double sum;

	for ( i = 0; i < n; i++ ) {
		X[ i ] = (double)i;
	}
	sum = c_dasum( n, X, stride );

	printf( "Result: %.2f\n", sum );
	return 0;
}
