!> Wraps dasum as a subroutine.
!
! @param {integer} N - number of values to sum
! @param {Array<double>} dx - input array
! @param {integer} stride - stride length
! @param {double} asum - output variable reference
!<
subroutine dasumsub( N, dx, stride, asum )
  implicit none
  ! ..
  ! External functions:
  external dasum
  double precision :: dasum
  ! ..
  ! Scalar arguments:
  integer :: stride, N
  double precision :: asum
  ! ..
  ! Array arguments:
  double precision :: dx(*)
  ! ..
  ! Invoke the external function:
  asum = dasum( N, dx, stride )
  return
end subroutine dasumsub