!> Wraps dasum as a subroutine.
!
! @param {integer} N - number of values to sum
! @param {Array<double>} dx - input array
! @param {integer} stride - stride length
! @param {double} sum - output variable reference
!<
subroutine dasumsub( N, dx, stride, sum )
  implicit none
  ! ..
  ! External functions:
  interface
    double precision function dasum( N, dx, stride )
      integer :: stride, N
      double precision :: dx(*)
    end function dasum
  end interface
  ! ..
  ! Scalar arguments:
  integer :: stride, N
  double precision :: sum
  ! ..
  ! Array arguments:
  double precision :: dx(*)
  ! ..
  ! Compute the sum of absolute values:
  sum = dasum( N, dx, stride )
  return
end subroutine dasumsub