'use strict';

var round = require( '@stdlib/math/base/special/round' );
var randu = require( '@stdlib/math/base/random/randu' );
var daxpy = require( './../lib' ).wasm();

var xbytes;
var ybytes;
var x;
var y;
var i;

// Allocate space on the heap:
xbytes = daxpy.malloc( 100 * 8 ); // 8 bytes per double
ybytes = daxpy.malloc( 100 * 8 );

// Create Float64Array views:
x = new Float64Array( xbytes.buffer, xbytes.byteOffset, 100 );
y = new Float64Array( ybytes.buffer, ybytes.byteOffset, 100 );

// Set data on the heap...
for ( i = 0; i < x.length; i++ ) {
	x[ i ] = round( randu()*100.0 );
	y[ i ] = round( randu()*10.0 );
}
console.log( daxpy( x.length, 5.0, xbytes, 1, 0, ybytes, -1, y.length-1 ) );

// Free allocated memory:
daxpy.free( xbytes );
daxpy.free( ybytes );
