'use strict';

// MODULES //

var hypot = require( '@stdlib/math/base/special/hypot' );


// MAIN //

/**
* Computes the absolute value of a complex number.
*
* @param {number} re - real component
* @param {number} im - imaginary component
* @returns {number} absolute value
*
* @example
* var v = cabs( 5.0, 3.0 );
* // returns ~5.83
*/
function cabs( re, im ) {
	return hypot( re, im );
}


// EXPORTS //

module.exports = cabs;
