'use strict';

// MODULES //

var real = require( '@stdlib/math/base/complex/real' );
var imag = require( '@stdlib/math/base/complex/imag' );
var hypot = require( '@stdlib/math/base/special/hypot' );


// MAIN //

/**
* Computes the absolute value of a complex number `z`.
*
* @param {Complex} z - input value
* @returns {number} absolute value
*
* @example
* var Complex128 = require( '@stdlib/types/complex128' );
*
* var z = new Complex128( 5.0, 3.0 );
* var v = cabs( z );
* // returns ~5.83
*/
function cabs( z ) {
	return hypot( real( z ), imag( z ) );
} // end FUNCTION cabs()


// EXPORTS //

module.exports = cabs;
