'use strict';

// MODULES //

var real = require( '@stdlib/math/base/complex/real' );
var imag = require( '@stdlib/math/base/complex/imag' );


// MAIN //

/**
* Computes the squared absolute value of a complex number `z`.
*
* ## Notes
*
* * Be careful of overflow and underflow.
*
*
* @param {Complex} z - input value
* @returns {number} squared absolute value
*
* @example
* var Complex128 = require( '@stdlib/types/complex128' );
*
* var z = new Complex128( 5.0, 3.0 );
* var v = cabs2( z );
* // returns 34.0
*/
function cabs2( z ) {
	var re = real( z );
	var im = imag( z );
	return (re*re) + (im*im);
} // end FUNCTION cabs2()


// EXPORTS //

module.exports = cabs2;
