'use strict';

/**
* Returns the imaginary component of a complex number.
*
* @param {Complex} z - complex number
* @returns {number} imaginary component
*
* @example
* var Complex128 = require( '@stdlib/types/complex/complex128' );
*
* var z = new Complex128( 5.0, 3.0 );
*
* var im = imag( z );
* // returns 3.0
*/
function imag( z ) {
	return z.im;
} // end FUNCTION imag()


// EXPORTS //

module.exports = imag;
