'use strict';

// MODULES //

var tape = require( 'tape' );
var Complex128 = require( '@stdlib/types/complex/complex128' );
var Complex64 = require( '@stdlib/types/complex/complex64' );
var imag = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof imag, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns the imaginary component of a complex number', function test( t ) {
	var im;
	var z;

	z = new Complex128( 3.14, -3.14 );
	im = imag( z );
	t.strictEqual( im, -3.14, 'returns expected value' );

	z = new Complex64( 3.14, -3.14 );
	im = imag( z );
	t.strictEqual( im, -3.140000104904175, 'returns expected value' );

	t.end();
});
