#!/usr/bin/env julia

import JSON

"""
    gen( re, im, name )

Generate fixture data and write to file.

# Arguments

* `re`: real components
* `im`: imaginary components
* `name::AbstractString`: output filename

# Examples

``` julia
julia> re = linspace( -1000.0, 1000.0, 2001 );
julia> im = linspace( 0.0, 1000.0, 1001 );
julia> gen( re, im, \"data.json\" );
```
"""
function gen( re, im, name )
	z = Array( Float64, length(re) );
	for i in eachindex(re)
		z[ i ] = angle( Complex128( re[i], im[i] ) );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("re", re),
		("im", im),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Random (re positive, im positive):
re = rand( 5001 ) * 500.0;
im = rand( 5001 ) * 500.0;
gen( re, im, "positive_positive.json" );

# Random (re negative, im positive):
re = -rand( 1001 ) * 500.0;
im = rand( 1001 ) * 500.0;
gen( re, im, "negative_positive.json" );

# Random (re negative, im negative):
re = -rand( 1001 ) * 500.0;
im = -rand( 1001 ) * 500.0;
gen( re, im, "negative_negative.json" );
