#!/usr/bin/env julia

import Distributions: pdf, Arcsine
import JSON

"""
	gen( x, a, b, name )

Generate fixture data and write to file.

# Arguments

* `x`: input value
* `a`: minimum support
* `b`: maximum support
* `name::AbstractString`: output filename

# Examples

``` julia
julia> x = rand( 1000 ) * 5.0;
julia> a = rand( 1000 ) * 10;
julia> b = a + rand( 1000 ) * 10.0;
julia> gen( x, a, b, "data.json" );
```
"""
function gen( x, a, b, name )
	z = Array( Float64, length(x) );
	for i in eachindex(x)
		z[ i ] = pdf( Arcsine( a[i], b[i] ), x[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("a", a),
		("b", b),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Small Range:
x = rand( 1000 ) * 100.0;
a = rand( 1000 ) * 20.0;
b = rand( 1000 ) * 20.0 + a;
gen( x, a, b, "small_range.json" );

# Medium Range:
x = rand( 1000 ) * 100.0;
a = rand( 1000 ) * 20.0;
b = rand( 1000 ) * 40.0 + a;
gen( x, a, b, "medium_range.json" );

# Large Range:
x = rand( 1000 ) * 100.0;
a = rand( 1000 ) * 20.0;
b = rand( 1000 ) * 80.0 + a;
gen( x, a, b, "large_range.json" );
