#!/usr/bin/env julia

import Distributions: cdf, Beta
import JSON

"""
	gen( x, alpha, beta, name )

Generate fixture data and write to file.

# Arguments

* `x`: input value
* `alpha`: first shape parameter
* `beta`: second shape parameter
* `name::AbstractString`: output filename

# Examples

``` julia
julia> x = rand( 1000 );
julia> alpha = rand( 1000 ) * 10.0;
julia> beta = rand( 1000 ) * 10.0;
julia> gen( x, alpha, beta, \"data.json\" );
```
"""
function gen( x, alpha, beta, name )
	z = Array( Float64, length(x) );
	for i in eachindex(x)
		z[ i ] = cdf( Beta( alpha[i], beta[i] ), x[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("alpha", alpha),
		("beta", beta),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Large alpha parameter:
x = rand( 1000 );
alpha = rand( 1000 ) * 10.0 + 10.0;
beta = rand( 1000 ) * 10.0;
gen( x, alpha, beta, "large_alpha.json" );

# Large rate parameter:
x = rand( 1000 );
alpha = rand( 1000 ) * 10.0;
beta = rand( 1000 ) * 10.0 + 10.0;
gen( x, alpha, beta, "large_beta.json" );

# Both large:
x = rand( 1000 );
alpha = rand( 1000 ) * 20.0 + 10.0;
beta = rand( 1000 ) * 20.0 + 10.0;
gen( x, alpha, beta, "both_large.json" );
