'use strict';

var Beta = require( './../lib' );

var dist;
var out;

dist = new Beta( 2.0, 4.0 );

out = dist.mean;
console.log( 'Mean = %d', out );
// => Mean = 0.3333333333333333

out = dist.median;
console.log( 'Median = %d', out );
// => Median = 0.3138101704556974

out = dist.variance;
console.log( 'Variance = %d', out );
// => Var = 0.031746031746031744

out = dist.cdf( 0.8 );
console.log( 'F(0.8) = %d', out );
// => F(0.8) = 0.99328

