'use strict';

// MAIN //

/**
* Returns the excess kurtosis of a beta distribution.
*
* @param {PositiveNumber} alpha - first shape parameter
* @param {PositiveNumber} beta - second shape parameter
* @returns {number} excess kurtosis
*
* @example
* var v = kurtosis( 1.0, 1.0 );
* // returns 0.0
* @example
* var v = kurtosis( 4.0, 12.0 );
* // returns ~0.529
* @example
* var v = kurtosis( 8.0, 2.0 );
* // returns ~-0.829
* @example
* var v = kurtosis( 1.0, -0.1 );
* // returns NaN
* @example
* var v = kurtosis( -0.1, 1.0 );
* // returns NaN
* @example
* var v = kurtosis( 2.0, NaN );
* // returns NaN
* @example
* var v = kurtosis( NaN, 2.0 );
* // returns NaN
*/
function kurtosis( alpha, beta ) {
	var atimesb;
	var aminusb;
	var aplusb;
	var out;

	if ( alpha <= 0.0 || beta <= 0.0 ) {
		return NaN;
	}
	atimesb = alpha * beta;
	aminusb = alpha - beta;
	aplusb = alpha + beta;
	out = ( aminusb * aminusb * ( aplusb + 1 ) );
	out -= atimesb * ( aplusb + 2.0 );
	out *= 6.0;
	out /= atimesb * ( aplusb + 2.0 ) * ( aplusb + 3.0 );
	return out;
} // end FUNCTION kurtosis()


// EXPORTS //

module.exports = kurtosis;
