'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var mean = require( './../lib' );

var alpha;
var beta;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	alpha = randu() * 10.0;
	beta = randu() * 10.0;
	v = mean( alpha, beta );
	console.log( 'α: %d, β: %d, E(X;α,β): %d',
		alpha.toFixed( 4 ),
		beta.toFixed( 4 ),
		v.toFixed( 4 )
	);
}

