'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var mode = require( './../lib' );

var alpha;
var beta;
var v;
var i;

for ( i = 0; i < 10; i++ ) {
	alpha = ( randu()*10.0 ) + 1.0;
	beta = ( randu()*10.0 ) + 1.0;
	v = mode( alpha, beta );
	console.log( 'α: %d, β: %d, mode(X;α,β): %d', alpha.toFixed( 4 ), beta.toFixed( 4 ), v.toFixed( 4 ) );
}

