'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var getMean = require( './../lib' );

var mean;
var i;
var n;
var p;

for ( i = 0; i < 10; i++ ) {
	n = round( randu() * 100 );
	p = randu();
	mean = getMean( n, p );
	console.log( 'n: %d, p: %d, E(X;n,p): %d',
		n,
		p.toFixed( 4 ),
		mean.toFixed( 4 )
	);
}

