'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var mean = require( './../lib' );


// FIXTURES //

var data = require( './fixtures/julia/data.json' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof mean, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the function returns `NaN`', function test( t ) {
	var v = mean( NaN, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mean( 10, NaN );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mean( NaN, NaN );
	t.equal( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided an `n` which is not a nonnegative integer, the function returns `NaN`', function test( t ) {
	var v;

	v = mean( 1.5, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mean( -2, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mean( -1, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mean( 2.5, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mean( PINF, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided a success probability `p` outside of `[0,1]`, the function returns `NaN`', function test( t ) {
	var v;

	v = mean( 20, -1.0 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mean( 20, 1.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mean( 20, NINF );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mean( 20, PINF );
	t.equal( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns the mean of a binomial distribution', function test( t ) {
	var expected;
	var n;
	var p;
	var y;
	var i;

	expected = data.expected;
	n = data.n;
	p = data.p;
	for ( i = 0; i < n.length; i++ ) {
		y = mean( n[i], p[i] );
		t.equal( y, expected[i], 'n: '+n[i]+', p: '+p[i]+', y: '+y+', expected: '+expected[i] );
	}
	t.end();
});
