'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var randu = require( '@stdlib/math/base/random/randu' );
var floor = require( '@stdlib/math/base/special/floor' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var mode = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof mode, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the function returns `NaN`', function test( t ) {
	var v = mode( NaN, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mode( 10, NaN );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mode( NaN, NaN );
	t.equal( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided an `n` which is not a nonnegative integer, the function returns `NaN`', function test( t ) {
	var v;

	v = mode( 1.5, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mode( -2, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mode( -1, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mode( 2.5, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mode( PINF, 0.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided a success probability `p` outside of `[0,1]`, the function returns `NaN`', function test( t ) {
	var v;

	v = mode( 20, -1.0 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mode( 20, 1.5 );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mode( 20, NINF );
	t.equal( isnan( v ), true, 'returns NaN' );

	v = mode( 20, PINF );
	t.equal( isnan( v ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns the mode of a binomial distribution', function test( t ) {
	var n;
	var p;
	var y;
	var i;

	for ( i = 0; i < 10; i++ ) {
		n = floor( randu() * 100.0 );
		p = randu();
		y = mode( n, p );
		t.equal( y, floor( ( n+1 ) * p ), 'returns mode' );
	}
	t.end();
});
