'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/utils/is-nan' );
var log1p = require( '@stdlib/math/base/special/log1p' );
var pow = require( '@stdlib/math/base/special/pow' );
var ln = require( '@stdlib/math/base/special/ln' );
var LNPI = require( '@stdlib/math/constants/float64-ln-pi' );


// PDF //

/**
* Evaluates the logarithm of the probability density function (PDF) for a Cauchy distribution with scale parameter `gamma` and location parameter `x0` at a value `x`.
*
* @param {number} x - input value
* @param {number} x0 - location parameter
* @param {PositiveNumber} gamma - scale parameter
* @returns {number} evaluated logPDF
*
* @example
* var y = logpdf( 2.0, 1.0, 1.0 )
* // returns ~-1.839
* @example
* var y = logpdf( 4.0, 3.0, 0.1 )
* // returns ~-3.458
* @example
* var y = logpdf( 4.0, 3.0, 3.0 )
* // returns ~-2.354
* @example
* var y = logpdf( NaN, 1.0, 1.0 )
* // returns NaN
* @example
* var y = logpdf( 2.0, NaN, 1.0 )
* // returns NaN
* @example
* var y = logpdf( 2.0, 1.0, NaN )
* // returns NaN
* @example
* // Negative scale parameter:
* var y = logpdf( 2.0, 1.0, -2.0 )
* // returns NaN
*/
function logpdf( x, x0, gamma ) {
	if (
		isnan( x ) ||
		isnan( gamma ) ||
		isnan( x0 )
	) {
		return NaN;
	}
	if ( gamma <= 0.0 ) {
		return NaN;
	}
	return -( LNPI + ln( gamma ) + log1p( pow( (x - x0) / gamma, 2.0 ) ) );
} // end FUNCTION logpdf()


// EXPORTS //

module.exports = logpdf;
