'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/utils/is-nan' );


// MAIN //

/**
* Returns the mode of a Cauchy distribution.
*
* @param {number} x0 - location parameter
* @param {PositiveNumber} gamma - scale parameter
* @returns {number} mode
*
* @example
* var v = mode( 10.0, 5.0 );
* // returns 10.0
* @example
* var v = mode( 7.0, 0.1 );
* // returns 7.0
* @example
* var v = mode( 10.0, -0.5 );
* // returns NaN
*/
function mode( x0, gamma ) {
	if (
		isnan( gamma ) ||
		isnan( x0 ) ||
		gamma <= 0.0
	) {
		return NaN;
	}
	return x0;
} // end FUNCTION mode()


// EXPORTS //

module.exports = mode;
