'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var quantile = require( './../lib' );

var gamma;
var x0;
var p;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	p = randu();
	x0 = randu() * 10.0 - 5.0;
	gamma = randu() * 20.0;
	y = quantile( p, gamma, x0 );
	console.log( 'p: %d, x0: %d, Î³: %d, Q(p;x0,Î³): %d',
		p.toFixed(4),
		x0.toFixed(4),
		gamma.toFixed(4),
		y.toFixed(4)
	);
}
