'use strict';

// MODULES //

var gammaCDF = require( '@stdlib/math/base/dist/gamma/cdf' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );


// MAIN //

/**
* Evaluates the cumulative distribution function (CDF) for a chi distribution with degrees of freedom `k` at a value `x`.
*
* @param {number} x - input value
* @param {NonNegativeNumber} k - degrees of freedom
* @returns {number} evaluated CDF
*
* @example
* var y = cdf( 2.0, 3.0 );
* // returns ~0.739
* @example
* var y = cdf( 1.0, 0.5 );
* // returns ~0.846
* @example
* var y = cdf( -1.0, 4.0 );
* // returns 0.0
* @example
* var y = cdf( NaN, 1.0 );
* // returns NaN
* @example
* var y = cdf( 0.0, NaN );
* // returns NaN
* @example
* // Negative degrees of freedom:
* var y = cdf( 2.0, -1.0 );
* // returns NaN
*/
function cdf( x, k ) {
	if (
		isnan( x ) ||
		isnan( k ) ||
		k < 0.0
	) {
		return NaN;
	}
	if ( k === 0.0 ) {
		return x <= 0 ? 0.0 : 1.0;
	}
	if ( x <= 0.0 ) {
		return 0.0;
	}
	return gammaCDF( x*x, k/2.0, 0.5 );
} // end FUNCTION cdf()


// EXPORTS //

module.exports = cdf;
