'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var mgf = require( './../lib' );

var t;
var k;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	t = randu() * 0.5;
	k = randu() * 10.0;
	y = mgf( t, k );
	console.log( 'x: %d, k: %d, M_X(t;k): %d', t.toFixed( 4 ), k.toFixed( 4 ), y.toFixed( 4 ) );
}
