'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );


// MAIN //

/**
* Evaluates the cumulative distribution function (CDF) for a degenerate distribution with mean value `mu`.
*
* @param {number} x - input value
* @param {number} mu - constant value of distribution
* @returns {Probability} evaluated cumulative distribution function
*
* @example
* var y = cdf( 2.0, 3.0 );
* // returns 0.0
* @example
* var y = cdf( 4.0, 3.0 );
* // returns 1.0
* @example
* var y = cdf( 3.0, 3.0 );
* // returns 1.0
* @example
* var y = cdf( NaN, 0.0 );
* // returns NaN
* @example
* var y = cdf( 0.0, NaN );
* // returns NaN
*/
function cdf( x, mu ) {
	if ( isnan( x ) || isnan( mu ) ) {
		return NaN;
	}
	return (x < mu) ? 0.0 : 1.0;
} // end FUNCTION cdf()


// EXPORTS //

module.exports = cdf;
