'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var cdf = require( './../lib/cdf.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof cdf, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the function returns `NaN`', function test( t ) {
	var y;

	y = cdf( NaN, 3.0 );
	t.equal( isnan( y ), true, 'returns NaN' );

	y = cdf( 0.0, NaN );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns `1.0` if `x` is greater or equal to `mu`', function test( t ) {
	var y;

	y = cdf( 3.0, 3.0 );
	t.equal( y, 1.0, 'returns 1.0' );

	y = cdf( 4.0, 3.0 );
	t.equal( y, 1.0, 'returns 1.0' );

	t.end();
});

tape( 'the created function returns `0.0` if `x` is smaller than `mu`', function test( t ) {
	var y;

	y = cdf( 2.9, 3.0 );
	t.equal( y, 0.0, 'returns 0.0' );

	y = cdf( -2.0, 3.0 );
	t.equal( y, 0.0, 'returns 0.0' );

	t.end();
});
