'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/utils/is-nan' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var nan = require( './nan.js' );


// MAIN //

/**
* Returns a function for evaluating the logarithm of the cumulative distribution function (CDF) of a degenerate distribution centered at a provided mean value.
*
* @param {number} mu - value at which to center the distribution
* @returns {Function} logCDF
*
* @example
* var logcdf = factory( 5 );
* var y = logcdf( 3 );
* // returns Number.NEGATIVE_INFINITY
* y = logcdf( 6 );
* // returns 0
* y = logcdf( NaN )
* // returns NaN
*/
function factory( mu ) {
	if ( isnan( mu ) ) {
		return nan;
	}
	return logcdf;

	/**
	* Evaluates the logarithm of the cumulative distribution function (CDF) of a degenerate distribution.
	*
	* @private
	* @param {number} x - input value
	* @returns {Probability} evaluated logarithm of cumulative distribution function
	*
	* @example
	* var y = logcdf( 10 );
	* // returns 0.0 || Number.NEGATIVE_INFINITY
	*/
	function logcdf( x ) {
		if ( isnan( x ) ) {
			return NaN;
		}
		return x < mu ? NINF : 0.0;
	} // end FUNCTION logcdf()
} // end FUNCTION factory()


// EXPORTS //

module.exports = factory;
