'use strict';

// MODULES //

var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var nan = require( './nan.js' );


// FACTORY //

/**
* Returns a function for evaluating the logarithm of the probability density function (PDF) of a degenerate distribution centered at a provided mean value.
*
* @param {number} mu - value at which to center the distribution
* @returns {Function} function to evaluate the logarithm of the probability density function
*
* @example
* var logpdf = factory( 5.0 );
* var y = logpdf( 0.0 );
* // returns Number.NEGATIVE_INFINITY
* y = logpdf( 5.0 );
* // returns Number.POSITIVE_INFINITY
*/
function factory( mu ) {
	if ( isnan( mu ) ) {
		return nan;
	}
	/**
	* Evaluates the logarithm of the probability density function (PDF) of a degenerate distribution.
	*
	* @private
	* @param {number} x - input value
	* @returns {number} evaluated logarithm of probability density function
	*
	* @example
	* var y = logpdf( 10.0 );
	* // returns Number.POSITIVE_INFINITY || Number.NEGATIVE_INFINITY
	*/
	return function logpdf( x ) {
		if ( isnan( x ) ) {
			return NaN;
		}
		return x === mu ? PINF : NINF;
	}; // end FUNCTION logpdf()
} // end FUNCTION factory()


// EXPORTS //

module.exports = factory;
