'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var factory = require( './../lib/factory.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof factory, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns a function', function test( t ) {
	var pdf = factory( 1.0 );
	t.equal( typeof pdf, 'function', 'returns a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the created function returns `NaN`', function test( t ) {
	var pdf;
	var y;

	pdf = factory( 0.0 );
	y = pdf( NaN );
	t.equal( isnan( y ), true, 'returns NaN' );

	pdf = factory( NaN );
	y = pdf( 0.0 );
	t.equal( isnan( y ), true, 'returns NaN' );

	t.end();
});

tape( 'the created function returns `+Infinity` if provided `x` equal to `mu`', function test( t ) {
	var pdf;
	var y;

	pdf = factory( 2.0 );
	y = pdf( 2.0 );
	t.equal( y, +Infinity, 'returns +Infinity' );

	pdf = factory( 0.0 );
	y = pdf( 0.0 );
	t.equal( y, +Infinity, 'returns +Infinity' );

	pdf = factory( -3.0 );
	y = pdf( -3.0 );
	t.equal( y, +Infinity, 'returns +Infinity' );

	t.end();
});

tape( 'the created function returns `0.0` if provided `x` not equal to `mu`', function test( t ) {
	var pdf;
	var y;

	pdf = factory( 3.0 );

	y = pdf( 2.0 );
	t.equal( y, 0.0, 'returns 0.0' );

	y = pdf( 4.0 );
	t.equal( y, 0.0, 'returns 0.0' );

	t.end();
});
