'use strict';

// MODULES //

var factoryGamma = require( '@stdlib/math/base/base/dist/gamma/cdf' ).factory;


// FACTORY //

/**
* Returns a function for evaluating the cumulative distribution function (CDF) for a Erlang distribution with shape parameter `k` and rate parameter `lambda`.
*
* @param {Number} k - shape parameter
* @param {Number} lambda - rate parameter
* @returns {Function} CDF
*/
function factory( k, lambda ) {
	return factoryGamma( k, lambda );
} // end FUNCTION factory()


// EXPORTS //

module.exports = factory;
