#!/usr/bin/env julia

using Distributions
import JSON

"""
	gen( x, k, lambda, name )

Generate fixture data and write to file.

# Arguments

* `x`: input value
* `k`: shape parameter
* `lambda`: rate parameter
* `name::AbstractString`: output filename

"""
function gen( x, k, lambda, name )
	z = Array( Float64, length(x) );
	for i in eachindex(x)
		z[ i ] = pdf( Erlang( k[i], 1/lambda[i] ), x[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("k", k),
		("lambda", lambda),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Large shape parameter:
x = rand( 1000 ) * 5.0;
k = round( rand( 1000 ) * 10.0 ) + 10;
lambda = rand( 1000 ) * 10.0;
gen( x, k, lambda, "large_shape.json" );

# Large rate parameter:
x = rand( 1000 ) * 5.0;
k = round( rand( 1000 ) * 10.0 );
lambda = rand( 1000 ) * 10.0 + 10.0;
gen( x, k, lambda, "large_rate.json" );

# Both large:
x = rand( 1000 ) * 5.0;
k = round( rand( 1000 ) * 20.0 ) + 10;
lambda = rand( 1000 ) * 20.0 + 10.0;
gen( x, k, lambda, "both_large.json" );
