'use strict';

// MODULES //

var factoryGamma = require( 'distributions-gamma-quantile/lib/factory.js' );


// FACTORY //

/**

*	Partially applies shape parameter `k` and rate parameter `lambda` and returns a function for evaluating the quantile function for a Erlang distribution.
*
* @param {Number} k - shape parameter
* @param {Number} lambda - rate parameter
* @returns {Function} quantile function
*/
function factory( k, lambda ) {
	return factoryGamma( k, lambda ); 
} // end FUNCTION factory()


// EXPORTS //

module.exports = factory;
