#!/usr/bin/env julia

using Distributions
import JSON

"""
    gen( x, lambda, name )

Generate fixture data and write to file.

# Arguments

* `x`: input value
* `lambda`: rate parameter
* `name::AbstractString`: output filename

# Examples

``` julia
julia> x = linspace( -1000, 1000, 2001 );
julia> y = linspace( 0, 1000, 1001 );
julia> gen( x, y, \"data.json\" );
```
"""
function gen( x, lambda, name )
	z = Array( Float64, length(x) );
	for i in eachindex(x)
		z[ i ] = pdf( Exponential( 1/lambda[i] ), x[i] );
	end

	# Store data to be written to file as a collection:
	data = Dict([
		("x", x),
		("lambda", lambda),
		("expected", z)
	]);

	# Based on the script directory, create an output filepath:
	filepath = joinpath( dir, name );

	# Write the data to the output filepath as JSON:
	outfile = open( filepath, "w" );
	write( outfile, JSON.json(data) );
	close( outfile );
end

# Get the filename:
file = @__FILE__;

# Extract the directory in which this file resides:
dir = dirname( file );

# Random (x small, lambda small):
x = rand( 1001 );
lambda = rand( 1001 );
gen( x, lambda, "small_small.json" );

# Random (x large, lambda small):
x = rand( 1001 ) * 100;
lambda = rand( 1001 );
gen( x, lambda, "large_small.json" );

# Random (x small, lambda large):
x = rand( 1001 );
lambda = rand( 1001 ) * 100;
gen( x, lambda, "small_large.json" );

# Random (x large, lambda large):
x = rand( 1001 ) * 100;
lambda = rand( 1001 ) * 100;
gen( x, lambda, "large_large.json" );
