# To run this script, `cd` to the `./test/fixtures` directory and then, from the R shell, `source("./runner.R")`.
options( digits = 16 )
library( RJSONIO )

set.seed( 778 )
lambda = sample( seq( 0.1, 100, length.out = 1000 )  )

x = sample( seq( -10, 200, length.out = 1000 ) )
y = dexp( x, lambda )

data = list(
	x = x,
	lambda = lambda,
	expected = y
)

write( toJSON( data, digits = 16, auto_unbox = TRUE ), "./data.json" )
