'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/utils/is-nan' );


// MAIN //

/**
* Returns the expected value of a geometric distribution.
*
* @param {Probability} p - success probability
* @returns {NonNegativeNumber} expected value
*
* @example
* var mean = getMean( 0.1 );
* // returns 9
* @example
* var mean = getMean( 0.5 );
* // returns 1
* @example
* var mean = getMean( 1.1 );
* // returns NaN
* @example
* var mean = getMean( NaN );
* // returns NaN
*/
function getMean( p ) {
	if (
		isnan( p ) ||
		p < 0.0 ||
		p > 1.0
	) {
		return NaN;
	}
	return ( 1.0 - p ) / p;
} // end FUNCTION getMean()


// EXPORTS //

module.exports = getMean;
