'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var NINF = require( '@stdlib/math/constants/float64-ninf' );
var getMean = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof getMean, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for `p`, the function returns `NaN`', function test( t ) {
	var mean = getMean( NaN );
	t.equal( isnan( mean ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided a success probability `p` outside of `[0,1]`, the function returns `NaN`', function test( t ) {
	var mean;

	mean = getMean( -1.0 );
	t.equal( isnan( mean ), true, 'returns NaN' );

	mean = getMean( 1.5 );
	t.equal( isnan( mean ), true, 'returns NaN' );

	mean = getMean( NINF );
	t.equal( isnan( mean ), true, 'returns NaN' );

	mean = getMean( PINF );
	t.equal( isnan( mean ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns the expected value for a geometric distribution with the given success probability `p`', function test( t ) {
	var mean = getMean( 0.5 );
	t.equal( mean, 1, 'returns 0.5/0.5' );

	mean = getMean( 0.1 );
	t.equal( mean, 9, 'returns 0.9/0.1' );

	t.end();
});
