'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var entropy = require( './../lib' );

var mu;
var β;
var y;
var i;

for ( i = 0; i < 10; i++ ) {
	mu = ( randu()*10.0 ) - 5.0;
	β = randu() * 20.0;
	y = entropy( mu, β );
	console.log( 'µ: %d, β: %d, h(X;µ,β): %d',
		mu.toFixed( 4 ),
		β.toFixed( 4 ),
		y.toFixed( 4 )
	);
}
