'use strict';

// MODULES //

var tape = require( 'tape' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var PINF = require( '@stdlib/math/constants/float64-pinf' );
var getMean = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof getMean, 'function', 'main export is a function' );
	t.end();
});

tape( 'if provided `NaN` for any parameter, the function returns `NaN`', function test( t ) {
	var mean = getMean( NaN, 10, 4 );
	t.equal( isnan( mean ), true, 'returns NaN' );

	mean = getMean( 20, NaN, 4 );
	t.equal( isnan( mean ), true, 'returns NaN' );

	mean = getMean( 20, 10, NaN );
	t.equal( isnan( mean ), true, 'returns NaN' );

	t.end();
});

tape( 'if provided a `N` which is not a non-negative integer, the function returns `NaN`', function test( t ) {
	var mean;

	mean = getMean( 10.5, 4, 2 );
	t.equal( isnan( mean ), true, 'returns NaN' );

	mean = getMean( -2, 4, 2 );
	t.equal( isnan( mean ), true, 'returns NaN' );

	mean = getMean( -1, 4, 2 );
	t.equal( isnan( mean ), true, 'returns NaN' );

	mean = getMean( 20.5, 10, 5 );
	t.equal( isnan( mean ), true, 'returns NaN' );

	mean = getMean( PINF, 10, 5 );
	t.equal( isnan( mean ), true, 'returns NaN' );

	t.end();
});

tape( 'the function returns the expected value for a hypergeometric distribution with the given parameters', function test( t ) {
	var mean = getMean( 10, 6, 4 );
	t.equal( mean, 2.4, 'returns 4 * (6/10)' );

	mean = getMean( 10, 9, 7 );
	t.equal( mean, 6.3, 'returns 7 * (9/10)' );

	t.end();
});
