'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var pmf = require( './../lib' );

var i;
var N;
var K;
var n;
var x;
var y;

for ( i = 0; i < 10; i++ ) {
	x = round( randu() * 5 );
	N = round( randu() * 20 );
	K = round( randu() * N );
	n = round( randu() * N );
	y = pmf( x, N, K, n );
	console.log( 'x: %d, N: %d, K: %d, n: %d, P(X=x;N,K,n): %d',
		x, N, K, n, y.toFixed( 4 )
	);
}
