'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var round = require( '@stdlib/math/base/special/round' );
var pmf = require( './../lib' );

var i;
var m;
var n;
var k;
var x;
var y;

for ( i = 0; i < 10; i++ ) {
	x = round( randu() * 5 );
	m = round( randu() * 20 );
	n = round( randu() * 20 );
	k = round( randu() * 10 );
	y = pmf( x, m, n, k );
	console.log( 'x: %d, m: %d, n: %d, k: %d, P(X=x;m,n,k): %d',
		x, m, n, k, y.toFixed( 4 )
	);
}
